import Foot from '@/components/loginpage/footer';
import genTestUserSig from "../../../public/debug/GenerateTestUserSig";
export default {
  components: {
    Foot: Foot
  },
  data: function data() {
    var validateUsername = function validateUsername(rule, value, callback) {
      if (!value) {
        return callback(new Error('用户名不能为空'));
      } else {
        callback();
      }
    };

    var validatePass = function validatePass(rule, value, callback) {
      if (value === '') {
        callback(new Error('请输入密码'));
      } else {
        callback();
      }
    };

    return {
      ruleForm: {
        username: '',
        password: ''
      },
      rules: {
        username: [{
          validator: validateUsername,
          trigger: 'blur'
        }],
        password: [{
          validator: validatePass,
          trigger: 'blur'
        }]
      }
    };
  },
  methods: {
    submitForm: function submitForm(formName) {
      var _this = this;

      console.log(this.ruleForm);
      this.$refs[formName].validate(function (valid) {
        if (valid) {
          _this.$http({
            url: '/worker/login',
            method: 'get',
            crossdomain: true,
            params: {
              username: _this.ruleForm.username,
              password: _this.ruleForm.password
            }
          }).then(function (res) {
            console.log(res.data);

            if (res.data.msg === '请求成功') {
              alert('登录成功!');

              _this.$store1.commit('setUsername', res.data.data.username);

              _this.$store1.commit('setUserID', res.data.data.id);

              _this.$store1.commit('setSchedule', res.data.data.schedule);

              var temp = res.data.data;
              temp.available = 1; // console.log(res.data.data)

              console.log(temp);

              _this.$http({
                url: '/admin/updateWorker',
                method: 'post',
                crossdomain: true,
                headers: {
                  'Content-Type': 'application/json'
                },
                body: JSON.stringify(temp)
              }).then(function (res) {
                console.log(res);
              }).catch(function (err) {
                console.log(err.data);
              });

              if (res.data.data.role == 3) {
                _this.$router.replace('/admin');
              } else if (res.data.data.role == 2) {
                _this.$router.replace('/helper');
              } else if (res.data.data.role == 1) {
                _this.$router.replace('/consultant');
              }

              _this.login_tim();
            } else {
              alert('用户名密码错误');
            }
          }).catch(function (err) {
            console.log('==', err.data);
          });
        } else {
          console.log('登录失败，请输入正确的用户名和密码');
          return false;
        }
      });
    },
    login_tim: function login_tim() {
      var _this2 = this;

      // this.loading = true
      console.log('t1');
      console.log(window.genTestUserSig(this.$store1.state.username).userSig);
      console.log(this.$store1.state.username);
      console.log(this.$store1.state.userid);
      this.tim.login({
        userID: this.$store1.state.username,
        userSig: window.genTestUserSig(this.$store1.state.username).userSig
      }).then(function () {
        _this2.loading = false;
        console.log('t1_inter');

        _this2.$store.commit('toggleIsLogin', true);

        _this2.$store.commit('startComputeCurrent');

        _this2.$store.commit('showMessage', {
          type: 'success',
          message: '登录成功'
        });

        _this2.$store.commit({
          type: 'GET_USER_INFO',
          userID: _this2.$store.state.username,
          userSig: window.genTestUserSig(_this2.$store1.state.username).userSig,
          sdkAppID: window.genTestUserSig('').SDKAppID
        });

        _this2.$store.commit('showMessage', {
          type: 'success',
          message: '登录成功'
        });
      }).catch(function (error) {
        _this2.loading = false;

        _this2.$store.commit('showMessage', {
          message: '登录失败：' + error.message,
          type: 'error'
        });
      });
    },
    resetForm: function resetForm(formName) {
      this.$refs[formName].resetFields();
    }
  }
};
